              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="Allowed">Allowed</SelectItem>
                <SelectItem value="Prohibit">Prohibit</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>

        <Button type="submit" className="w-full">Save Changes</Button>
