              />
              <Button
                type="button"
                onClick={() => {
                  onBalanceAction("freeze", balanceActions.freeze);
                  setBalanceActions({ ...balanceActions, freeze: "" });
                }}
                className="rounded-l-none bg-warning hover:bg-warning/90"
              >
                <Lock className="w-4 h-4" />
