  const [selectedDuration, setSelectedDuration] = useState("60");
  const { user } = useAuth();
  const { toast } = useToast();

  // Set crypto based on selectedCoin parameter or default to BTC
  const [selectedCrypto, setSelectedCrypto] = useState(
